var esmLangHe = false;

function esmTranslateTitle(title) {
  if (!esmLangHe) return title;
  if (ESM_HE.tree[title]) return ESM_HE.tree[title];
  var t = title;
  var keys = Object.keys(ESM_HE.artKeywords).sort(function(a,b) { return b.length - a.length; });
  for (var i = 0; i < keys.length; i++) {
    if (t.indexOf(keys[i]) >= 0) {
      t = t.split(keys[i]).join(ESM_HE.artKeywords[keys[i]]);
    }
  }
  return t;
}

function esmToggleHebrew() {
  esmLangHe = !esmLangHe;
  try { localStorage.setItem('esm_lang_he', esmLangHe ? '1' : '0'); } catch(e) {}
  
  var btn = document.getElementById('heBtn');
  if (btn) btn.textContent = esmLangHe ? 'EN' : 'עברית';
  
  esmTranslateNav();
  esmTranslateTree();
  esmTranslateArticles();
}

function esmTranslateNav() {
  try {
    var navDoc = document.getElementById('F11').contentDocument;
    if (!navDoc) return;
    
    var labels = navDoc.querySelectorAll('.Label');
    for (var i = 0; i < labels.length; i++) {
      var el = labels[i];
      var origText = el.getAttribute('data-orig') || el.textContent.trim();
      if (!el.getAttribute('data-orig')) el.setAttribute('data-orig', origText);
      
      if (esmLangHe && ESM_HE.ui[origText]) {
        el.textContent = ESM_HE.ui[origText];
        el.style.direction = 'rtl';
      } else {
        el.textContent = origText;
        el.style.direction = '';
      }
    }
    
    // Translate radio labels
    var radioLabels = navDoc.querySelectorAll('label');
    for (var i = 0; i < radioLabels.length; i++) {
      var el = radioLabels[i];
      var origText = el.getAttribute('data-orig') || el.textContent.trim();
      if (!el.getAttribute('data-orig')) el.setAttribute('data-orig', origText);
      
      if (esmLangHe && ESM_HE.ui[origText]) {
        el.textContent = ESM_HE.ui[origText];
      } else {
        el.textContent = origText;
      }
    }
  } catch(e) { console.log('Nav translate error:', e); }
}

function esmTranslateTree() {
  try {
    var navDoc = document.getElementById('F11').contentDocument;
    if (!navDoc) return;
    var treeFrame = navDoc.getElementById('iframeTree');
    if (!treeFrame || !treeFrame.contentDocument) return;
    var treeDoc = treeFrame.contentDocument;
    
    // Tree items are in <nobr> tags inside list items
    var items = treeDoc.querySelectorAll('nobr, .tree-text, td');
    for (var i = 0; i < items.length; i++) {
      var el = items[i];
      // Only translate text nodes, skip elements with children that have text
      var textNodes = [];
      for (var c = 0; c < el.childNodes.length; c++) {
        if (el.childNodes[c].nodeType === 3 && el.childNodes[c].textContent.trim()) {
          textNodes.push(el.childNodes[c]);
        }
      }
      for (var t = 0; t < textNodes.length; t++) {
        var node = textNodes[t];
        var origText = node._origText || node.textContent.trim();
        if (!node._origText) node._origText = origText;
        
        if (esmLangHe && ESM_HE.tree[origText]) {
          node.textContent = ESM_HE.tree[origText];
        } else if (!esmLangHe && node._origText) {
          node.textContent = node._origText;
        }
      }
    }
  } catch(e) { console.log('Tree translate error:', e); }
}

function esmTranslateArticles() {
  try {
    var f12 = document.getElementById('F12');
    if (!f12 || !f12.contentDocument) return;
    var doc = f12.contentDocument;
    
    // Article titles are in anchor tags or td elements
    var links = doc.querySelectorAll('a, td.ViewerTD, .top_title, .servinfosub_title b');
    for (var i = 0; i < links.length; i++) {
      var el = links[i];
      var origText = el.getAttribute('data-orig') || el.textContent.trim();
      if (!origText || origText.length < 3) continue;
      if (!el.getAttribute('data-orig')) el.setAttribute('data-orig', origText);
      
      if (esmLangHe) {
        var translated = esmTranslateTitle(origText);
        if (translated !== origText) {
          el.textContent = translated;
          el.style.direction = 'rtl';
          el.style.unicodeBidi = 'plaintext';
        }
      } else {
        el.textContent = origText;
        el.style.direction = '';
        el.style.unicodeBidi = '';
      }
    }
  } catch(e) { console.log('Articles translate error:', e); }
}

// Auto-translate when iframes load
function esmWatchFrames() {
  try {
    var f11 = document.getElementById('F11');
    if (f11) {
      f11.addEventListener('load', function() {
        if (esmLangHe) { setTimeout(esmTranslateNav, 300); }
        // Watch for tree iframe changes inside nav
        try {
          var navDoc = f11.contentDocument;
          var treeFrame = navDoc.getElementById('iframeTree');
          if (treeFrame) {
            treeFrame.addEventListener('load', function() {
              if (esmLangHe) setTimeout(esmTranslateTree, 300);
            });
          }
        } catch(e) {}
      });
    }
    
    var f12 = document.getElementById('F12');
    if (f12) {
      f12.addEventListener('load', function() {
        if (esmLangHe) setTimeout(esmTranslateArticles, 300);
      });
    }
  } catch(e) {}
}

// Initialize
(function() {
  try {
    if (localStorage.getItem('esm_lang_he') === '1') esmLangHe = true;
  } catch(e) {}
  
  window.addEventListener('load', function() {
    esmWatchFrames();
    if (esmLangHe) {
      var btn = document.getElementById('heBtn');
      if (btn) btn.textContent = 'EN';
      setTimeout(function() {
        esmTranslateNav();
        esmTranslateTree();
      }, 1000);
    }
  });
})();
